        // player2 = []

        getGrid = () => {
            let grid = []
            for(let i = 0; i < squares.length; i++){
                grid.push(<Square key={i.toString()} id={i} value={squares[i]}>0</Square>)
            }
            return grid
        }
    render(){

        //create a lines for the SQUARES of the grid ex 1px solid, black

        let grid = squares.map((box, i) => {
             return (
                 <Square key={i.toString()} id={i} value={box}>0</Square>
             )
        })

      return (
        <div>
            <div  className = "grid">
                {grid}
            </div>   
        </div>

      );
    }
  }



export default Board;
